// generated by gong
import { Component, OnInit, AfterViewInit, ViewChild, Inject, Optional, Input } from '@angular/core';
import { BehaviorSubject } from 'rxjs'
import { MatSort } from '@angular/material/sort';
import { MatPaginator } from '@angular/material/paginator';
import { MatTableDataSource } from '@angular/material/table';
import { MatButton } from '@angular/material/button'

import { MatDialogRef, MAT_DIALOG_DATA, MatDialog } from '@angular/material/dialog'
import { DialogData, FrontRepoService, FrontRepo, SelectionMode } from '../front-repo.service'
import { NullInt64 } from '../null-int64'
import { SelectionModel } from '@angular/cdk/collections';

const allowMultiSelect = true;

import { ActivatedRoute, Router, RouterState } from '@angular/router';
import { TableStyleDB } from '../tablestyle-db'
import { TableStyleService } from '../tablestyle.service'

// insertion point for additional imports

import { RouteService } from '../route-service';

// TableComponent is initilizaed from different routes
// TableComponentMode detail different cases 
enum TableComponentMode {
  DISPLAY_MODE,
  ONE_MANY_ASSOCIATION_MODE,
  MANY_MANY_ASSOCIATION_MODE,
}

// generated table component
@Component({
  selector: 'app-tablestylestable',
  templateUrl: './tablestyles-table.component.html',
  styleUrls: ['./tablestyles-table.component.css'],
})
export class TableStylesTableComponent implements OnInit {

  @Input() GONG__StackPath: string = ""

  // mode at invocation
  mode: TableComponentMode = TableComponentMode.DISPLAY_MODE

  // used if the component is called as a selection component of TableStyle instances
  selection: SelectionModel<TableStyleDB> = new (SelectionModel)
  initialSelection = new Array<TableStyleDB>()

  // the data source for the table
  tablestyles: TableStyleDB[] = []
  matTableDataSource: MatTableDataSource<TableStyleDB> = new (MatTableDataSource)

  // front repo, that will be referenced by this.tablestyles
  frontRepo: FrontRepo = new (FrontRepo)

  // displayedColumns is referenced by the MatTable component for specify what columns
  // have to be displayed and in what order
  displayedColumns: string[];

  // for sorting & pagination
  @ViewChild(MatSort)
  sort: MatSort | undefined
  @ViewChild(MatPaginator)
  paginator: MatPaginator | undefined;

  ngAfterViewInit() {

    // enable sorting on all fields (including pointers and reverse pointer)
    this.matTableDataSource.sortingDataAccessor = (tablestyleDB: TableStyleDB, property: string) => {
      switch (property) {
        case 'ID':
          return tablestyleDB.ID

        // insertion point for specific sorting accessor
        case 'Name':
          return tablestyleDB.Name;

        case 'Node':
          return (tablestyleDB.Node ? tablestyleDB.Node.Name : '');

        case 'Content':
          return tablestyleDB.Content;

        case 'Val':
          return tablestyleDB.Val;

        default:
          console.assert(false, "Unknown field")
          return "";
      }
    };

    // enable filtering on all fields (including pointers and reverse pointer, which is not done by default)
    this.matTableDataSource.filterPredicate = (tablestyleDB: TableStyleDB, filter: string) => {

      // filtering is based on finding a lower case filter into a concatenated string
      // the tablestyleDB properties
      let mergedContent = ""

      // insertion point for merging of fields
      mergedContent += tablestyleDB.Name.toLowerCase()
      if (tablestyleDB.Node) {
        mergedContent += tablestyleDB.Node.Name.toLowerCase()
      }
      mergedContent += tablestyleDB.Content.toLowerCase()
      mergedContent += tablestyleDB.Val.toLowerCase()

      let isSelected = mergedContent.includes(filter.toLowerCase())
      return isSelected
    };

    this.matTableDataSource.sort = this.sort!
    this.matTableDataSource.paginator = this.paginator!
  }

  applyFilter(event: Event) {
    const filterValue = (event.target as HTMLInputElement).value;
    this.matTableDataSource.filter = filterValue.trim().toLowerCase();
  }

  constructor(
    private tablestyleService: TableStyleService,
    private frontRepoService: FrontRepoService,

    // not null if the component is called as a selection component of tablestyle instances
    public dialogRef: MatDialogRef<TableStylesTableComponent>,
    @Optional() @Inject(MAT_DIALOG_DATA) public dialogData: DialogData,

    private router: Router,
    private activatedRoute: ActivatedRoute,

    private routeService: RouteService,
  ) {

    // compute mode
    if (dialogData == undefined) {
      this.mode = TableComponentMode.DISPLAY_MODE
    } else {
      this.GONG__StackPath = dialogData.GONG__StackPath
      switch (dialogData.SelectionMode) {
        case SelectionMode.ONE_MANY_ASSOCIATION_MODE:
          this.mode = TableComponentMode.ONE_MANY_ASSOCIATION_MODE
          break
        case SelectionMode.MANY_MANY_ASSOCIATION_MODE:
          this.mode = TableComponentMode.MANY_MANY_ASSOCIATION_MODE
          break
        default:
      }
    }

    // observable for changes in structs
    this.tablestyleService.TableStyleServiceChanged.subscribe(
      message => {
        if (message == "post" || message == "update" || message == "delete") {
          this.getTableStyles()
        }
      }
    )
    if (this.mode == TableComponentMode.DISPLAY_MODE) {
      this.displayedColumns = ['ID', 'Delete', // insertion point for columns to display
        "Name",
        "Node",
        "Content",
        "Val",
      ]
    } else {
      this.displayedColumns = ['select', 'ID', // insertion point for columns to display
        "Name",
        "Node",
        "Content",
        "Val",
      ]
      this.selection = new SelectionModel<TableStyleDB>(allowMultiSelect, this.initialSelection);
    }

  }

  ngOnInit(): void {
    let stackPath = this.activatedRoute.snapshot.paramMap.get('GONG__StackPath')
    if (stackPath != undefined) {
      this.GONG__StackPath = stackPath
    }

    this.getTableStyles()

    this.matTableDataSource = new MatTableDataSource(this.tablestyles)
  }

  getTableStyles(): void {
    this.frontRepoService.pull(this.GONG__StackPath).subscribe(
      frontRepo => {
        this.frontRepo = frontRepo

        this.tablestyles = this.frontRepo.TableStyles_array;

        // insertion point for time duration Recoveries
        // insertion point for enum int Recoveries

        // in case the component is called as a selection component
        if (this.mode == TableComponentMode.ONE_MANY_ASSOCIATION_MODE) {
          for (let tablestyle of this.tablestyles) {
            let ID = this.dialogData.ID
            let revPointer = tablestyle[this.dialogData.ReversePointer as keyof TableStyleDB] as unknown as NullInt64
            if (revPointer.Int64 == ID) {
              this.initialSelection.push(tablestyle)
            }
            this.selection = new SelectionModel<TableStyleDB>(allowMultiSelect, this.initialSelection);
          }
        }

        if (this.mode == TableComponentMode.MANY_MANY_ASSOCIATION_MODE) {

          let mapOfSourceInstances = this.frontRepo[this.dialogData.SourceStruct + "s" as keyof FrontRepo] as Map<number, TableStyleDB>
          let sourceInstance = mapOfSourceInstances.get(this.dialogData.ID)!

          // we associates on sourceInstance of type SourceStruct with a MANY MANY associations to TableStyleDB
          // the field name is sourceField
          let sourceFieldArray = sourceInstance[this.dialogData.SourceField as keyof typeof sourceInstance]! as unknown as TableStyleDB[]
          if (sourceFieldArray != null) {
            for (let associationInstance of sourceFieldArray) {
              let tablestyle = associationInstance[this.dialogData.IntermediateStructField as keyof typeof associationInstance] as unknown as TableStyleDB
              this.initialSelection.push(tablestyle)
            }
          }

          this.selection = new SelectionModel<TableStyleDB>(allowMultiSelect, this.initialSelection);
        }

        // update the mat table data source
        this.matTableDataSource.data = this.tablestyles
      }
    )
  }

  // newTableStyle initiate a new tablestyle
  // create a new TableStyle objet
  newTableStyle() {
  }

  deleteTableStyle(tablestyleID: number, tablestyle: TableStyleDB) {
    // list of tablestyles is truncated of tablestyle before the delete
    this.tablestyles = this.tablestyles.filter(h => h !== tablestyle);

    this.tablestyleService.deleteTableStyle(tablestyleID, this.GONG__StackPath).subscribe(
      tablestyle => {
        this.tablestyleService.TableStyleServiceChanged.next("delete")
      }
    );
  }

  editTableStyle(tablestyleID: number, tablestyle: TableStyleDB) {

  }

  // set editor outlet
  setEditorRouterOutlet(tablestyleID: number) {
    let outletName = this.routeService.getEditorOutlet(this.GONG__StackPath)
    let fullPath = this.routeService.getPathRoot() + "-" + "tablestyle" + "-detail"

    let outletConf: any = {}
    outletConf[outletName] = [fullPath, tablestyleID, this.GONG__StackPath]

    this.router.navigate([{ outlets: outletConf }])
  }

  /** Whether the number of selected elements matches the total number of rows. */
  isAllSelected() {
    const numSelected = this.selection.selected.length;
    const numRows = this.tablestyles.length;
    return numSelected === numRows;
  }

  /** Selects all rows if they are not all selected; otherwise clear selection. */
  masterToggle() {
    this.isAllSelected() ?
      this.selection.clear() :
      this.tablestyles.forEach(row => this.selection.select(row));
  }

  save() {

    if (this.mode == TableComponentMode.ONE_MANY_ASSOCIATION_MODE) {

      let toUpdate = new Set<TableStyleDB>()

      // reset all initial selection of tablestyle that belong to tablestyle
      for (let tablestyle of this.initialSelection) {
        let index = tablestyle[this.dialogData.ReversePointer as keyof TableStyleDB] as unknown as NullInt64
        index.Int64 = 0
        index.Valid = true
        toUpdate.add(tablestyle)

      }

      // from selection, set tablestyle that belong to tablestyle
      for (let tablestyle of this.selection.selected) {
        let ID = this.dialogData.ID as number
        let reversePointer = tablestyle[this.dialogData.ReversePointer as keyof TableStyleDB] as unknown as NullInt64
        reversePointer.Int64 = ID
        reversePointer.Valid = true
        toUpdate.add(tablestyle)
      }


      // update all tablestyle (only update selection & initial selection)
      for (let tablestyle of toUpdate) {
        this.tablestyleService.updateTableStyle(tablestyle, this.GONG__StackPath)
          .subscribe(tablestyle => {
            this.tablestyleService.TableStyleServiceChanged.next("update")
          });
      }
    }

    if (this.mode == TableComponentMode.MANY_MANY_ASSOCIATION_MODE) {

      // get the source instance via the map of instances in the front repo
      let mapOfSourceInstances = this.frontRepo[this.dialogData.SourceStruct + "s" as keyof FrontRepo] as Map<number, TableStyleDB>
      let sourceInstance = mapOfSourceInstances.get(this.dialogData.ID)!

      // First, parse all instance of the association struct and remove the instance
      // that have unselect
      let unselectedTableStyle = new Set<number>()
      for (let tablestyle of this.initialSelection) {
        if (this.selection.selected.includes(tablestyle)) {
          // console.log("tablestyle " + tablestyle.Name + " is still selected")
        } else {
          console.log("tablestyle " + tablestyle.Name + " has been unselected")
          unselectedTableStyle.add(tablestyle.ID)
          console.log("is unselected " + unselectedTableStyle.has(tablestyle.ID))
        }
      }

      // delete the association instance
      let associationInstance = sourceInstance[this.dialogData.SourceField as keyof typeof sourceInstance]
      let tablestyle = associationInstance![this.dialogData.IntermediateStructField as keyof typeof associationInstance] as unknown as TableStyleDB
      if (unselectedTableStyle.has(tablestyle.ID)) {
        this.frontRepoService.deleteService(this.dialogData.IntermediateStruct, associationInstance)


      }

      // is the source array is empty create it
      if (sourceInstance[this.dialogData.SourceField as keyof typeof sourceInstance] == undefined) {
        (sourceInstance[this.dialogData.SourceField as keyof typeof sourceInstance] as unknown as Array<TableStyleDB>) = new Array<TableStyleDB>()
      }

      // second, parse all instance of the selected
      if (sourceInstance[this.dialogData.SourceField as keyof typeof sourceInstance]) {
        this.selection.selected.forEach(
          tablestyle => {
            if (!this.initialSelection.includes(tablestyle)) {
              // console.log("tablestyle " + tablestyle.Name + " has been added to the selection")

              let associationInstance = {
                Name: sourceInstance["Name"] + "-" + tablestyle.Name,
              }

              let index = associationInstance[this.dialogData.IntermediateStructField + "ID" as keyof typeof associationInstance] as unknown as NullInt64
              index.Int64 = tablestyle.ID
              index.Valid = true

              let indexDB = associationInstance[this.dialogData.IntermediateStructField + "DBID" as keyof typeof associationInstance] as unknown as NullInt64
              indexDB.Int64 = tablestyle.ID
              index.Valid = true

              this.frontRepoService.postService(this.dialogData.IntermediateStruct, associationInstance)

            } else {
              // console.log("tablestyle " + tablestyle.Name + " is still selected")
            }
          }
        )
      }

      // this.selection = new SelectionModel<TableStyleDB>(allowMultiSelect, this.initialSelection);
    }

    // why pizza ?
    this.dialogRef.close('Pizza!');
  }
}
